import styled from 'styled-components';

import BaseContainer from '../components/Container';
import Button from '../components/Button';
import Input from '../components/Input';
import { useParams } from 'react-router-dom';
import { numberWithCommas } from '../utils';
import { products } from '../data';
import { useEffect, useState } from 'react';

const Container = styled(BaseContainer)`
  padding-top: 78px;
  padding-bottom: 78px;
  display: grid;
  grid-template-columns: 1fr;

  @media screen and (min-width: 768px) {
    grid-template-columns: 1fr 1fr;
    gap: 96px;
  }
`;

const ProductImage = styled.img`
  width: 100%;
`;

const ProductInfo = styled.div`
  display: flex;
  flex-direction: column;
`;

const Title = styled.div`
  font-size: 40px;
  line-height: 60px;
  font-weight: 600;
  margin-bottom: 72px;
`;

const Subtitle = styled.div`
  display: flex;
  justify-content: space-between;
  font-size: 32px;
  line-height: 48px;
  font-weight: 400;
  color: #000000;
`;

const Description = styled.p`
  font-size: 24px;
  line-height: 36px;
  font-weight: 300;

  color: #000000;
  margin-top: 0;
  margin-bottom: 72px;
`;

// const data = products[0];

/**
 * Below is the main ProductDetail component.
 */

export const ProductDetail = () => {
  const {productId} = useParams()
  const [products, setProducts] = useState([])
const [loading, setLoading] = useState(false)
console.log('products :>> ', products);
const data = products;
useEffect(() => {
  setLoading(true)
  fetch("https://us-central1-skooldio-react-hooks.cloudfunctions.net/products/" + productId)
  .then((resp) => resp.json())
  .then((data) => {
    console.log('data :>> ', data);
    setProducts(data)
    setLoading(false)
  })
}, [])
  return (
    <Container>
      {loading && <div>Loading</div>}
      {!loading && <>
      <ProductImage src={data.imageUrl} alt={`${data.name}`} />
      <ProductInfo>
        <Subtitle>
          <span>{data.category}</span>
          <span>à¸¿{numberWithCommas(data.price)}</span>
        </Subtitle>
        <Title>{data.name}</Title>
        <Description>{data.description}</Description>
        <Input style={{ marginBottom: '40px' }} type={'number'} label={'Quantity'} />
        <Button>Add to Cart</Button>
      </ProductInfo> 
      </>}
    </Container>
  );
}


export default ProductDetail;
